import React, { useState, useEffect } from 'react';
//import './RoomLobby.css';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import InboxIcon from '@material-ui/icons/Inbox';
import DraftsIcon from '@material-ui/icons/Drafts';
import styled from 'styled-components';
import { withStyles } from '@material-ui/core/styles';
import { TextareaAutosize } from '@material-ui/core';

import { BrowserRouter as Router, Route, Switch, Redirect, useHistory, useParams, useLocation, History, Link } from "react-router-dom";
import { createBrowserHistory } from "history";

import axios from "axios";

var memberList;

async function getUserData(roomNum){
  const roomData = await axios.get('http://localhost:5000/rooms/getRoom' + '?room=' + encodeURIComponent(roomNum), false)
	.then((response) => {
	    console.log(response.data);
	    memberList = response.data[0].members;
  });
}
const StyledTextField = styled(TextField)`
  label.focused {
    color: #ffffff;
  }
  .MuiOutlinedInput-root {
    fieldset {
      border-color: #83A3BC;
    }
    &:hover fieldset {
      border-color: #294662;
    }
    &.Mui-focused fieldset {
      border-color: #294662;
    }
  }
  textInputStyle: {
    color: 'white',
  }
`;

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: 360,
    backgroundColor: theme.palette.background.paper,
  },
}));

function ListItemLink(props) {
    return <ListItem button component="a" {...props} />;
}

function RoomLobby() {
  const history = useHistory();
  const classes = useStyles();
  const location = useLocation();
  const {number} = useParams();
  const [items, setItems] = useState([]);

  useEffect(() => {
  	console.log(memberList);
     setItems(memberList);
  }, []);

  const routeChange = () =>{ 
    let path = "/"; 
    history.push(path);
  }

  // <Grid container spacing={3}>
  //     <div style={{ marginLeft: "28%", marginTop: "20%", marginBottom: "100%", marginRight: "28%" }}>
  //       <Grid item xs={12} style={{padding: "5%", marginLeft: "15%"}} >
  //         <Button variant="outlined" borderColor="white" onClick={routeChange}>Go Back</Button>
  //       </Grid>
  //       <Grid item xs={12} style={{padding: "5%", marginLeft: "15%"}} >
  //         <Button variant="outlined" borderColor="white" onClick={getUserData(location.state)}>Get Data</Button>
  //       </Grid>
  //     </div>
  //     <div> {memberList} </div>
  //   </Grid>
  getUserData(location.state);

  return (
  	<div className={classes.root} style={{ marginLeft: "28%", marginTop: "20%", marginBottom: "100%", marginRight: "28%" }}>
  	  <Button variant="outlined" bordercolor="white" onClick={routeChange}>Go Back</Button>
      <List component="nav" aria-label="secondary mailbox folders">
        {items.map((item, index) => (
          <ListItem>
        	<ListItemText primary= {item} />
          </ListItem>
      	))}
      </List>
    </div>
  )
}
export default RoomLobby;
