import React from 'react';
import './Signin.css';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import styled from 'styled-components';
import { withStyles } from '@material-ui/core/styles';
import { TextareaAutosize } from '@material-ui/core';

import { BrowserRouter as Router, Route, Switch, Redirect, useHistory, History, Link } from "react-router-dom";
import { createBrowserHistory } from "history";

import axios from "axios";

function createUserRoom(){
  axios.post('http://localhost:5000/users/makeUser', {
    username: document.getElementById('userNameVal').value,
    roomCode: document.getElementById('roomCodeVal').value
  })
  .then(function (response) {
    console.log(response);
  })
  .catch(function (error) {
    console.log(error);
  });
  var array = ["room", document.getElementById('userNameVal').value]
  axios.post('http://localhost:5000/rooms/addRoom', {
    roomCode: document.getElementById('roomCodeVal').value,
    members: array,
    numMembers: 1,
  })
  .then(function (response) {
    console.log(response);
  })
  .catch(function (error) {
    console.log(error);
  });

}

function updateUserRoom(){
  axios.post('http://localhost:5000/users/makeUser', {
    username: document.getElementById('userNameVal').value,
    roomCode: document.getElementById('roomCodeVal').value
  })
    .then(function (response) {
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
}

const StyledTextField = styled(TextField)`
  label.focused {
    color: #ffffff;
  }
  .MuiOutlinedInput-root {
    fieldset {
      border-color: #83A3BC;
    }
    &:hover fieldset {
      border-color: #294662;
    }
    &.Mui-focused fieldset {
      border-color: #294662;
    }
  }
  textInputStyle: {
    color: 'white',
  }
`;

const useStyles = makeStyles({
  root: {
    //background: 'linear-gradient(45deg, #FE6B8B 30%, #FF8E53 90%)',
    border: 0,
    borderRadius: 5,
    boxShadow: '0 3px 5px 2px rgba(0, 0, 0, .3)',
    color: 'white',
    height: 500,
    width: 400,
    margin: 30,
    padding: '0 30px'
  },
  centeredcard: {
    marginLeft: 95,
    marginRight: 95,
    marginBottom: 20,
    marginTop: 30
  },
  title: {
    fontSize: 43,
    fontFamily: 'Helvetica',
    color: '#000000'
  },
  titlecontent: {
    marginBottom: -20
  },
  textbox: {
    padding: 10
  },
});

function Signin() {
  const history = useHistory();
  const classes = useStyles();

  const routeChange = () =>{ 
    let path = "/RoomLobby"; 
    history.push(path);
    createUserRoom();
  }

  return (
    <Card className={classes.root} variant="outlined">
      <CardContent>
        <Grid container spacing={3}>
        <div className={classes.centeredcard}>
          <Grid item xs={12} className={classes.titlecontent}>
            <h1 className={classes.title}>
              Groceries
            </h1>
          </Grid>
          <Grid item xs={12} className={classes.textbox}>
            <StyledTextField id="userNameVal" label="Name" variant="outlined" />
          </Grid>
          <Grid item xs={12} className={classes.textbox}>
            <StyledTextField id="roomCodeVal" label="Room Code" variant="outlined" />
          </Grid>
          <Grid item xs={12} style={{padding: "5%", marginLeft: "12%"}} >
            <Button variant="outlined" borderColor="white" onClick={routeChange}>Create Room</Button>
          </Grid>
          <Grid item xs={12} style={{padding: "5%", marginLeft: "18%"}} >
            <Button variant="outlined" borderColor="white" onClick={updateUserRoom}>Join Room</Button>
          </Grid>
        </div>
        </Grid>
      </CardContent>
    </Card>
  )
}
export default Signin;
