import React, { useState, useEffect } from 'react';
//import './RoomLobby.css';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import CssBaseline from '@material-ui/core/CssBaseline';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Paper from '@material-ui/core/Paper';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import styled from 'styled-components';
import { withStyles } from '@material-ui/core/styles';
import { TextareaAutosize } from '@material-ui/core';

import { BrowserRouter as Redirect, useHistory, useParams, useLocation, History } from "react-router-dom";
import { createBrowserHistory } from "history";

import axios from "axios";

var currentRoom;
var memberList;
var groceryList;
var user;

async function addGrocery() {
  const newGrocery = await axios.post('http://localhost:5000/groceries/addGrocery', {
    name: document.getElementById('name').value,
    roomCode: currentRoom,
    message: document.getElementById('message').value,
    requester: user,
  })
  .then(function (response) {
    console.log(response);
  })
  .catch(function (error) {
    console.log(error);
  });
}

const StyledTextField = styled(TextField)`
  label.focused {
    color: #ffffff;
  }
  .MuiOutlinedInput-root {
    fieldset {
      border-color: #83A3BC;
    }
    &:hover fieldset {
      border-color: #294662;
    }
    &.Mui-focused fieldset {
      border-color: #294662;
    }
  }
  textInputStyle: {
    color: 'white',
  }
`;

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    maxWidth: 360,
    borderRadius: 5,
    boxShadow: '0px 5px 5px 2px rgba(0, 0, 0, .3)',
    backgroundColor: theme.palette.background.paper,
  },
  text: {
    color: '#222222',
    fontFamily: 'Raleway',
    fontWeights: 'bold',
    fontSize: 20,
  },
  title: {
  	color: '#444444',
  	fontFamily: 'Raleway',
    fontSize: 43,
  },
  centered: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center"
  },
}));

function ListItemLink(props) {
    return <ListItem button component="a" {...props} />;
}

function RoomLobby() {
  const history = useHistory();
  const classes = useStyles();
  const location = useLocation();

  // room and user data
  user = location.state.user;
  currentRoom = location.state.number;

  // member list data 
  const [items, setItems] = useState([]);

  // grocery list data 
  const [gItems, setGItems] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/rooms/getRoom' + '?room=' + encodeURIComponent(location.state.number), false)
	.then((response) => {
	    console.log(response.data);
	    memberList = response.data[0].members;
	    setItems(memberList);
  	})
  	.catch(function (error) {
    	console.log(error);
  	});

  }, []);
  useEffect(() => {
  	axios.get('http://localhost:5000/groceries/getGroceries' + '?room=' + encodeURIComponent(location.state.number), false)
	.then((response) => {
	    console.log(response.data);
	    groceryList = []
	    for(var i in response.data) {
	    	groceryList.push(response.data[i]);
	    }
	    console.log(groceryList);
	    setGItems(groceryList);
  	})
  	.catch(function (error) {
    	console.log(error);
  	});

  }, []);

  // Add grocery
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleCloseAdd = () => {
    setOpen(false);
    addGrocery();
  };

  //back
  const routeChange = () =>{ 
    let path = "/"; 
    history.push(path);
  }

  return (
  	<React.Fragment>

  	<div className={classes.root} style={{ marginLeft: "-60%", marginBottom: "25%", marginTop: "15%", marginRight: "10%" }}>
  	   <Grid container spacing={3}>
          <Grid item xs={12}>
            <Typography variant="h3" className={classes.title} style={{ marginLeft: "15%" }}>Member List</Typography>
            <Divider />
          	<List>
        		{items.map((item, index) => (
          		<ListItem key={index} item={item} className={classes.centered}>
        			<ListItemText classes={{primary:classes.text}} primary= {item} />
          		</ListItem>
      			))}
      		</List>
          </Grid>
          <Grid item xs={12} className={classes.titlecontent}>
            <Button variant="outlined" bordercolor="white" onClick={routeChange}>Go Back</Button>
          </Grid>
        </Grid>
    </div>

    <div className={classes.root} style={{ marginLeft: "20%", marginBottom: "25%", marginTop: "15%", marginRight: "10%" }}>
      <Grid container spacing={3}>
          <Grid item xs={12}>
            <Typography variant="h3" className={classes.title} style={{ marginLeft: "15%" }}>Grocery List</Typography>
            <Divider />
          	<List>
        		{gItems.map((item, index) => (
          		<ListItem key={index} item={item} className={classes.centered}>
        			<ListItemText classes={{primary:classes.text}} primary= {item.name} />
          		</ListItem>
      			))}
      		</List>
          </Grid>
      </Grid>

      <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Add grocery
      </Button>
      <Dialog open={open} onClose={handleClose} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Add a Grocery</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Put the grocery name and a message if you want!
          </DialogContentText>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Grocery Name"
            type="typename"
            fullWidth
          />
          <TextField
            autoFocus
            margin="dense"
            id="message"
            label="Message (optional)"
            type="typemessage"
            fullWidth
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose} color="primary">
            Cancel
          </Button>
          <Button onClick={handleCloseAdd} color="primary">
            Add
          </Button>
        </DialogActions>
      </Dialog>
    </div>
    </React.Fragment>
  )
}
export default RoomLobby;
